const client = new Client({
  authStrategy: new LocalAuth({ clientId: 'Bot-Mwsm' }),
  puppeteer: { headless: true,
    args: [
      '--no-sandbox',
      '--disable-setuid-sandbox',
      '--disable-dev-shm-usage',
      '--disable-accelerated-2d-canvas',
      '--no-first-run',
      '--no-zygote',
      '--single-process', // <- this one doesn't works in Windows
      '--disable-gpu'
    ] }
});

client.initialize();

io.on('connection', function(socket) {
  socket.emit('message', '> Bot-Mwsm : Loading application');
  socket.emit('qr', 'data:image/png;base64,');

client.on('qr', (qr) => {
    console.log('QR RECEIVED', qr);
    qrcode.toDataURL(qr, (err, url) => {
      socket.emit('qr', url);
      socket.emit('message', '> Bot-Mwsm : QRCode Received, Waiting to Read!');
      console.log('> Bot-Mwsm : QRCode Received, Waiting to Read!');
    });
});

client.on('ready', () => {
    socket.emit('ready', '> Bot-Mwsm : Reading Completed!');
    socket.emit('message', '> Bot-Mwsm : Reading Completed!');
    socket.emit('qr', 'data:image/png;base64,')	
    console.log('> Bot-Mwsm : Reading Completed!');
});

client.on('authenticated', () => {
    socket.emit('authenticated', '> Bot-Mwsm : Whatsapp Authenticated Successfully!');
    socket.emit('message', '> Bot-Mwsm : Whatsapp Authenticated Successfully!');
    console.log('> Bot-Mwsm : Whatsapp Authenticated Successfully!');
});

client.on('auth_failure', function() {
    socket.emit('message', '> Bot-Mwsm : Authentication Failed, Restarting...');
    console.log('> Bot-Mwsm : Authentication Failed, Restarting...');
});

client.on('change_state', state => {
  socket.emit('message', '> Bot-Mwsm : Status > ', state );
  console.log('> Bot-Mwsm : Status > ', state );
});

client.on('disconnected', (reason) => {
  socket.emit('qr', 'data:image/png;base64,"')
  socket.emit('message', '> Bot-Mwsm : Whatsapp Has Been Disconnected!');
  console.log('> Bot-Mwsm : Whatsapp Has Been Disconnected!');
  client.initialize();
});
});

// Send message
app.post('/send-message', [
  body('to').notEmpty(),
  body('msg').notEmpty(),
], async (req, res) => {
  const errors = validationResult(req).formatWith(({
    msg
  }) => {
    return msg;
  });

  if (!errors.isEmpty()) {
    return res.status(422).json({
      status: false,
      message: errors.mapped()
    });
  }

  const number = req.body.to;
  const numberDDI = number.substr(0, 2);
  const numberDDD = number.substr(2, 2);
  const numberUser = number.substr(-8, 8);
  const Message = req.body.msg;
  const Jumper =  Message.replaceAll("\\n","##").split("##");
  var WhatsApp = number + "@c.us";

  if (numberDDI === "55" && parseInt(numberDDD) <= 30) {
    WhatsApp = "55" + numberDDD + "9" + numberUser + "@c.us";
  }
  else if (numberDDI === "55" && parseInt(numberDDD) > 30) {
    WhatsApp = "55" + numberDDD + numberUser + "@c.us";
  }

for (let i=0; i<= Jumper.length -1; i++)  {
    Mensagem = Jumper[i];
    client.sendMessage(WhatsApp, Mensagem).then(response => {
    res.status(200).json({
      status: true,
      message: 'Bot-Mwsm : Message Sent',
      response: response
    });
    }).catch(err => {
    res.status(500).json({
      status: false,
      message: 'Bot-Mwsm : Message was not Sent',
      response: err.text
    });
    });
}

});


client.on('message', async msg => {

  const nomeContato = msg._data.notifyName;
  let groupChat = await msg.getChat();
  var msgbody = msg.body.toLowerCase();
  
  if (msg.type.toLowerCase() == "e2e_notification") return null;
  if (msg.body == "") return null;
  if (msg.from.includes("@g.us")) return null;


Object.values(msgbot).forEach((messageconfig) => {
  if (!messageconfig.active) return;

  const haskeyword = messageconfig.keywords.some((keys) =>
   keys.every((word) => msgbody.includes(word))
  );
  if (!haskeyword) return;

  if(messageconfig.message == false) exit;

  msg.reply(messageconfig.message);
});
});

